import 'package:get/get.dart';import 'package:service_app/core/theme/app_themes.dart';class AppController extends GetxController {  AppTheme _appTheme = AppTheme.fromType(ThemeType.light);  bool _isLoading = false;  AppTheme get appTheme => _appTheme;  bool get isLoading => _isLoading;  @override  void onReady() {    updateTheme(Get.isDarkMode ? AppTheme.fromType(ThemeType.dark) : AppTheme.fromType(ThemeType.light));    super.onReady();  }  updateTheme(theme) {    _appTheme = theme;    Get.forceAppUpdate();  }  void showLoading() {    _isLoading = true;    update();  }  void hideLoading() {    _isLoading = false;    update();  }}