import 'dart:io';import 'package:connectivity_plus/connectivity_plus.dart';import 'package:get/get.dart';import 'package:service_app/core/configuration/app_routes.dart';import 'index.dart';checkApiValidationError(data) {  dynamic error = data['errors'];  if (error != null) {    List keys = error.keys.toList();    if (keys.isNotEmpty) {      var msg = '';      for (int i = 0; i < keys.length; i++) {        String key = keys[i].toString();        if (i > 0) msg += '\n';        msg += error[key][0].toString();      }      snackBar(msg, duration: 'long');    } else {      snackBar(data['message'], duration: 'long');    }  } else {    snackBar(data['message'], duration: 'long');  }}apiExceptionMethod(controllerName, e) async {  hideLoading();  snackBar(trans('something_went_wrong'));}Future<bool> isNetworkConnection() async {  var connectivityResult = await Connectivity().checkConnectivity();  if (connectivityResult == ConnectivityResult.none) {    return false;  } else {    final result = await InternetAddress.lookup('google.com');    if (result.isNotEmpty && result[0].rawAddress.isNotEmpty) {      return true;    } else {      return false;    }  }}goToNoInternetScreen() {  String currentRoute = Get.currentRoute;  if (currentRoute != AppRoutes.noInternet) {    Get.toNamed(AppRoutes.noInternet);  }  snackBar(AppRoutes);}